import React, { useState } from "react";

export default function TextForm(props) {

    const handleUpClick = () =>
        {
        let newText = text.toUpperCase();
            setText(newText);
        }
    const handleLowClick = () =>
        {
        let newText = text.toLowerCase();
            setText(newText);
        }

    const handleClearClick = () =>
        {
        let newText = "";
            setText(newText);
        }
        
    const handleCopy= () =>
        {
          let text = document.getElementsById("myBox");
          text.select();
          navigator.clipboard.writeText(text.value);
        }    
    const handleSpeakClick = () =>
        {
        let msgText = new SpeechSynthesisUtterance();
        msgText.text = text;
        window.speechSynthesis.speak(msgText); 
        }    


    const handeleOnChange = (event) =>
        {
            setText(event.target.value);
        }

    const [text, setText] = useState("");
    // text = "new text"; wrong way to change the text
    // setText = "new text"; correct way to change the text

  return (
    <>
    <div className="conatiner">
      <h1>{props.heading} </h1>
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">
        </label>
        <textarea className="form-control" value ={text} onChange={handeleOnChange} id="myBox" rows="8"></textarea>
        </div>
      <button className="btn btn-primary mx-1" onClick={handleUpClick}>Convert to UpperCase</button>
      <button className="btn btn-primary mx-1" onClick={handleLowClick}>Convert to LowerCase</button>
      <button className="btn btn-primary mx-1 my-1" onClick={handleClearClick}>Clear All Text</button>
      <button type="submit" className="btn btn-warning mx-1 my-1" onClick={handleSpeakClick}><i class="fa-solid fa-volume-low"></i> Speak</button>
      <button className="btn btn-primary mx-1 my-1" onClick={handleCopy}><i class="fa-solid fa-copy"></i></button>

    </div>

    <div className="container my-3">
      <h2>Your text Summary</h2>
      <p>
        Words: {text.split(" ").length} and Characters: {text.length} 
        <p className="my-1">Speaking time: {0.041 * text.split(" ").length}/wpm</p>
        <p> Reading time: {0.008 * text.split(" ").length}/wpm</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </p>
    </div>
    </>

  );
}
