import "./App.css";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import About from "./components/about";
import { useState } from "react";
import Alert from "./components/Alert";
function App() {

  const [mode, setMode] = useState("light"); // whether dark mode is enable or not 
  const [alert, setAlert] = useState(null);

  const showAlert = (message, type) =>
  {
      setAlert({
        msg :message,
        type:type
      })
  }

  const [btnMode,setBtnMode] = useState("dark"); 
  const toggelMode = ( ) =>
  {
    if(mode === "light")
    {
      setMode ("dark");
      setBtnMode ("light");
      document.body.style.color = "white";
      document.body.style.backgroundColor = "#272727";

    }
    else
    {
      setMode  ("light");
      setBtnMode  ("dark");
      document.body.style.color = "black";
      document.body.style.backgroundColor = "White";
    }
  }

  return (
    <>
    <Navbar title="TextUtils" about ="About Us" toggelMode={toggelMode}  mode={mode} btnMode={btnMode}></Navbar>
    <Alert alert="this is a alert"></Alert>
    <div className="container">
      <TextForm heading ="Enter the text to analyze" mode={mode} btnMode={btnMode}></TextForm>
    </div>
    <About toggelMode={toggelMode} mode={mode}></About>
   </>
  );
}

export default App;
